SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_atendimento_apae ( nr_seq_apae_p bigint, nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_prescricao_w		bigint;
ie_gerar_lote_w		varchar(15) := 'S';
cd_setor_atendimento_w	integer;
cd_pessoa_atend_w	varchar(10);
cd_pessoa_apae_w	varchar(10);
					

BEGIN 
 
ie_gerar_lote_w := obter_param_usuario(874, 13, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_gerar_lote_w);
 
if	(nr_seq_apae_p > 0 AND nr_atendimento_p > 0) then 
 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_atend_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_apae_w 
	from	aval_pre_anestesica 
	where	nr_sequencia = nr_seq_apae_p;
	 
	if (cd_pessoa_atend_w <> cd_pessoa_apae_w) then 
		-- O paciente informado não é o mesmo do atendimento! 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(264572);
	end if;	
	 
 
	update	aval_pre_anestesica 
	set	nr_atendimento	=	nr_atendimento_p 
	where	nr_sequencia	=	nr_seq_apae_p 
	and	coalesce(nr_atendimento::text, '') = '';
	 
	select	coalesce(max(nr_prescricao),0) 
	into STRICT	nr_prescricao_w 
	from	conclusao_recom_apae 
	where	nr_seq_aval_pre	=	nr_seq_apae_p;
	 
	if (nr_prescricao_w > 0) then 
		update	prescr_medica 
		set	nr_atendimento	= nr_atendimento_p 
		where	nr_prescricao 	= nr_prescricao_w;	
	end if;
	 
	if (ie_gerar_lote_w = 'S') then 
		cd_setor_atendimento_w := obter_setor_atendimento(nr_atendimento_p);
		 
		update	prescr_medica 
		set	cd_setor_atendimento	= cd_setor_atendimento_w 
		where	nr_prescricao 		= nr_prescricao_w;	
		 
		update	ap_lote 
		set	cd_setor_atendimento	= cd_setor_atendimento_w 
		where	ie_status_lote 		= 'G' 
		and	nr_prescricao		= nr_prescricao_w;	
		 
		CALL Gerar_Lote_Atend_Prescricao(nr_prescricao_w, 0, 0, 'N', nm_usuario_p, 'VAA');
	end if;
	 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
