CREATE OR REPLACE PROCEDURE vincular_atendimento_prescr ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nm_usuario_p usuario.nm_usuario%type ) AS $body$
DECLARE


nr_prescricao_w		prescr_medica.nr_prescricao%type;

c01 CURSOR FOR
	SELECT	nr_prescricao
	from	prescr_medica
	where	coalesce(nr_atendimento::text, '') = ''
	and		coalesce(dt_suspensao::text, '') = ''
	and		dt_prescricao	>= (clock_timestamp() - interval '24 days'/24)
	and		dt_prescricao	<= clock_timestamp()
	and		cd_pessoa_fisica = cd_pessoa_fisica_p;


BEGIN

open	c01;
loop
fetch	c01 into
		nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

		update	prescr_medica
		set		nr_atendimento = 	nr_atendimento_p,
				dt_atualizacao = 	clock_timestamp(),
				nm_usuario = 		nm_usuario_p
		where	nr_prescricao = 	nr_prescricao_w
		and		coalesce(nr_atendimento::text, '') = ''
		and		coalesce(dt_suspensao::text, '') = '';

		CALL CPOE_Vincular_atend_prescr(nr_prescricao_w, nr_atendimento_p, nm_usuario_p);

	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
