CREATE OR REPLACE PROCEDURE vincular_atendimento_same_js ( nr_sequencia_p text, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_aux_w	varchar(4000) := '';
nr_sequencia_w		varchar(10) := '';
k			integer;


BEGIN

nr_sequencia_aux_w := nr_sequencia_p;

while	(nr_sequencia_aux_w IS NOT NULL AND nr_sequencia_aux_w::text <> '') loop
	begin
	select	position(';' in nr_sequencia_aux_w)
	into STRICT	k
	;

	if (k > 1) and ((substr(nr_sequencia_aux_w, 1, k -1) IS NOT NULL AND (substr(nr_sequencia_aux_w, 1, k -1))::text <> '')) then
		nr_sequencia_w		:= substr(nr_sequencia_aux_w, 1, k-1);
		nr_sequencia_w		:= replace(nr_sequencia_w, ';','');
		nr_sequencia_aux_w	:= substr(nr_sequencia_aux_w, k + 1, 4000);
	elsif (nr_sequencia_aux_w IS NOT NULL AND nr_sequencia_aux_w::text <> '') then
		nr_sequencia_w		:= replace(nr_sequencia_aux_w,';','');
		nr_sequencia_aux_w	:= '';
	end if;

	update 	same_prontuario
	set 	nr_atendimento = nr_atendimento_p
	where 	nr_sequencia = nr_sequencia_w;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
