CREATE OR REPLACE PROCEDURE vincular_atend_auto_tto ( nr_seq_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


						
cd_pessoa_fisica_w	prescr_medica.cd_pessoa_fisica%type;
nr_atendimento_w	prescr_medica.nr_atendimento%type;
nr_prescricao_w		prescr_medica.nr_prescricao%type;
						

BEGIN

select	max(a.cd_pessoa_fisica)	
into STRICT	cd_pessoa_fisica_w						
from	prescr_medica a
where	a.nr_prescricao in (	SELECT	max(b.nr_prescricao)
								from	paciente_atendimento b
								where	b.nr_seq_atendimento = nr_seq_atendimento_p);
								
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	nr_atendimento_w := obter_ultimo_atendimento_estab(cd_pessoa_fisica_w, cd_estabelecimento_p);		
end if;						

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then

	CALL vincular_atend_quimioterapia(nr_seq_atendimento_p, nr_atendimento_w);	
	
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
