CREATE OR REPLACE PROCEDURE vincular_atend_cirurgia (nr_atendimento_p bigint, nr_cirurgia_p bigint, nm_usuario_p text, nr_seq_pepo_p bigint default 0) AS $body$
BEGIN
if (coalesce(nr_atendimento_p,0) > 0) and (coalesce(nr_cirurgia_p,0) > 0) then
	if (coalesce(nr_seq_pepo_p,0) > 0) then 
		update	pepo_cirurgia 
		set 	nr_atendimento =  nr_atendimento_p
		where 	nr_sequencia  = nr_seq_pepo_p
		and 	coalesce(nr_atendimento::text, '') = '';
	end if;
	
	update	cirurgia
	set 	nr_atendimento =  nr_atendimento_p
	where 	nr_cirurgia = nr_cirurgia_p
	and 	coalesce(nr_atendimento::text, '') = '';
	
	update	prescr_medica 
	set 	nr_atendimento =  nr_atendimento_p
	where 	nr_prescricao = (	
								SELECT max(nr_prescricao) 
								from cirurgia 
								where 	nr_cirurgia = nr_cirurgia_p
							) 
	and 	coalesce(nr_atendimento::text, '') = '';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
