CREATE OR REPLACE PROCEDURE vincular_atend_fila_espera ( cd_pessoa_fisica_p text, nr_atendimento_p bigint) AS $body$
DECLARE


ie_permite_vicular_w		varchar(255);
nr_sequencia_w			bigint;
ie_vinculou_w			varchar(255);


BEGIN

ie_permite_vicular_w := Obter_param_Usuario(916, 914, obter_perfil_ativo, Obter_Usuario_Ativo, obter_estabelecimento_ativo, ie_permite_vicular_w);

if ( coalesce(ie_permite_vicular_w,'N') = 'S' ) then

	select 	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	paciente_espera
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(nr_atendimento::text, '') = '';

	if (nr_sequencia_w > 0) then

		update	paciente_espera
		set	nr_atendimento	= nr_atendimento_p
		where	nr_sequencia	= nr_sequencia_w;

	end if;

end if;


commit;

end;
