CREATE OR REPLACE PROCEDURE vincular_atend_hemoterapia ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, ie_origem_p text) AS $body$
DECLARE


cd_pessoa_fisica_w varchar(10);

/*
ie_origem_p:
D - SAN_DOACAO
R - SAN_RESERVA
*/
BEGIN

cd_pessoa_fisica_w := cd_pessoa_fisica_p;

if ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (coalesce(nr_atendimento_p,0) > 0) and (coalesce(cd_pessoa_fisica_w::text, '') = '')) then
  	select cd_pessoa_fisica
  	into STRICT   cd_pessoa_fisica_w
 	 from   atendimento_paciente
 	 where  nr_atendimento = nr_atendimento_p;
end if;

if	((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (coalesce(nr_atendimento_p,0) > 0) and (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '')) then
	begin
	if (ie_origem_p = 'D') then
		begin
		update	san_doacao
		set	nr_atendimento 		= nr_atendimento_p
		where	cd_pessoa_fisica 	= cd_pessoa_fisica_w;
		end;
	elsif (ie_origem_p = 'R') then
			begin
			update	san_reserva
			set	nr_atendimento = nr_atendimento_p
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;
			end;
	end if;
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
