SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_atend_triagem_pa ( nr_atendimento_p bigint, nr_seq_triagem_p bigint, nm_usuario_p text) AS $body$
DECLARE

			
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	
	if (obtain_user_locale(nm_usuario_p) = 'de_DE') then
		select	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_fisica_w
		from	atendimento_paciente
		where	nr_atendimento = nr_atendimento_p
		and		coalesce(dt_alta::text, '') = '';
	else
		select	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_fisica_w
		from	atendimento_paciente
		where	nr_atendimento = nr_atendimento_p
		and		coalesce(dt_alta::text, '') = ''
		and		ie_tipo_atendimento = 3;
	end if;
	
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
		begin
		
		update	triagem_pronto_atend
		set 	nr_atendimento = nr_atendimento_p,
				dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p
		where 	cd_pessoa_fisica = cd_pessoa_fisica_w
        and     nr_sequencia = nr_seq_triagem_p;
		
		commit;
		
		CALL atualiza_atendimento_triagem(nr_seq_triagem_p, nm_usuario_p);
		
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
