CREATE OR REPLACE PROCEDURE vincular_atividade_intervencao ( nr_seq_intervencao_p bigint, nr_seq_atividade_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_existe_w	bigint;


BEGIN

select	count(*)
into STRICT	ie_existe_w
from	pe_intervencao_atividade
where	nr_seq_proc	= nr_seq_intervencao_p
and	nr_seq_atividade= nr_seq_atividade_p;

if (ie_existe_w = 0) then
	insert into pe_intervencao_atividade(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_proc,
		nr_seq_atividade)
	SELECT	nextval('pe_intervencao_atividade_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_intervencao_p,
		nr_seq_atividade_p
	;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
