CREATE OR REPLACE PROCEDURE vincular_ativ_intervencoes ( nr_seq_atividade_p bigint, ds_lista_intervencoes_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_intervencoes_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_seq_intervencao_w	bigint;


BEGIN
if (nr_seq_atividade_p IS NOT NULL AND nr_seq_atividade_p::text <> '') and (ds_lista_intervencoes_P IS NOT NULL AND ds_lista_intervencoes_P::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	ds_lista_intervencoes_w := ds_lista_intervencoes_p;

	while(ds_lista_intervencoes_w IS NOT NULL AND ds_lista_intervencoes_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in ds_lista_intervencoes_w);
		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_intervencao_w	:= (substr(ds_lista_intervencoes_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_intervencoes_w	:= substr(ds_lista_intervencoes_w,nr_pos_virgula_w+1,length(ds_lista_intervencoes_w));
			end;
		else
			begin
			nr_seq_intervencao_w	:= (ds_lista_intervencoes_w)::numeric;
			ds_lista_intervencoes_w	:= null;
			end;
		end if;

		if (coalesce(nr_seq_intervencao_w,0) > 0) then
			begin
			CALL vincular_atividade_intervencao(
				nr_seq_intervencao_w,
				nr_seq_atividade_p,
				nm_usuario_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;
