CREATE OR REPLACE PROCEDURE vincular_autor_cir_agenda_pac (nr_seq_autorizacao_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_estagio_autor_w	varchar(1);
dt_agenda_w		timestamp;

BEGIN

update	autorizacao_cirurgia
set	nr_seq_agenda	= nr_seq_agenda_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_autorizacao_p;

begin
select	dt_agenda
into STRICT	dt_agenda_w
from	agenda_paciente
where	nr_sequencia = nr_seq_agenda_p;
exception
when others then
	dt_agenda_w := null;
end;

update	autorizacao_convenio
set	nr_seq_agenda		= coalesce(nr_seq_agenda,nr_seq_agenda_p),
	dt_agenda		= coalesce(Dt_agenda,dt_agenda_w),
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_seq_autor_cirurgia	= nr_seq_autorizacao_p;

select	max(ie_estagio_autor)
into STRICT	ie_estagio_autor_w
from	autorizacao_cirurgia
where	nr_sequencia	= nr_seq_autorizacao_p;

if (ie_estagio_autor_w in ('3','8','7')) then --Autorizado, e atualizar o est√°gio no OPME
	CALL atualizar_agenda_pac_opme(nr_seq_autorizacao_p, nr_seq_agenda_p, nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
