CREATE OR REPLACE PROCEDURE vincular_conta_pac_parcial ( nr_sequencia_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, cd_setor_p bigint) AS $body$
BEGIN

update	procedimento_paciente a
set	a.nr_seq_parcial		= nr_sequencia_p
where	a.cd_setor_atendimento	= cd_setor_p
and	a.dt_procedimento		between dt_inicial_p and dt_final_p
and	(a.cd_motivo_exc_conta IS NOT NULL AND a.cd_motivo_exc_conta::text <> '')
and 	exists (	SELECT	1
		from	conta_paciente b
		where	a.nr_interno_conta = b.nr_interno_conta
		and	b.ie_status_acerto = 1);


update	material_atend_paciente a
set	a.nr_seq_parcial		= nr_sequencia_p
where	a.cd_setor_atendimento	= cd_setor_p
and	a.dt_atendimento		between dt_inicial_p and dt_final_p
and	(a.cd_motivo_exc_conta IS NOT NULL AND a.cd_motivo_exc_conta::text <> '')
and	exists (	SELECT	1
		from	conta_paciente	b
		where	b.nr_interno_conta = b.nr_interno_conta
		and	b.ie_status_acerto = 1);

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
