CREATE OR REPLACE PROCEDURE vincular_contrato_ordem_compra ( nr_ordem_compra_p bigint, nr_seq_contrato_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
vl_total_ordem_w			double precision;
vl_saldo_contrato_w		double precision;


BEGIN 
 
select	coalesce(obter_valor_liquido_ordem(nr_ordem_compra),0), 
	coalesce(obter_saldo_contrato_oc(nr_seq_contrato_p),0) 
into STRICT	vl_total_ordem_w, 
	vl_saldo_contrato_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
if (vl_total_ordem_w > vl_saldo_contrato_w) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(193062);
end if;
 
 
update	ordem_compra 
set	nr_seq_contrato = nr_seq_contrato_p 
where	nr_ordem_compra = nr_ordem_compra_p;
 
CALL inserir_historico_ordem_compra( 
	nr_ordem_compra_p, 
	'', 
	WHEB_MENSAGEM_PCK.get_texto(279116), 
	WHEB_MENSAGEM_PCK.get_texto(298016,'NR_SEQ_CONTRATO_P=' || nr_seq_contrato_p), 
	nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
