CREATE OR REPLACE PROCEDURE vincular_desvincular_apae ( nr_seq_apae_p bigint, nr_cirurgia_p bigint, ie_opcao_p text) AS $body$
BEGIN

if (ie_opcao_p = 'V') then

	update	aval_pre_anestesica
	set	nr_cirurgia	=	nr_cirurgia_p
	where	nr_sequencia	=	nr_seq_apae_p;

elsif (ie_opcao_p = 'D') then

	update	aval_pre_anestesica
	set	nr_cirurgia	 = NULL,
		nr_seq_pepo      = NULL
	where	nr_sequencia	=	nr_seq_apae_p;

end if;

commit;

end;
$body$
