CREATE OR REPLACE PROCEDURE vincular_doc_tec_item_solic ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_mat_item_solic_compra_w		bigint;
nr_sequencia_doc_tec_w		bigint;
seq_item_doc_tec_w		bigint;
nr_item_solic_compra_w		solic_compra_item.nr_item_solic_compra%type;

c00 CURSOR FOR
	SELECT 	cd_material,
			nr_item_solic_compra
	from	solic_compra_item
	where	nr_solic_compra = nr_solic_compra_p
	and	(((coalesce(nr_item_solic_compra_p,0) <> 0) and (nr_item_solic_compra = nr_item_solic_compra_p))
	or (coalesce(nr_item_solic_compra_p,0) =  0))
	order by nr_item_solic_compra;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	lic_material_doc_tec
	where	cd_material = cd_mat_item_solic_compra_w
	and (cd_estabelecimento = cd_estabelecimento_p
	or    	coalesce(cd_estabelecimento::text, '') = '')
	order by nr_sequencia;


BEGIN

open C00;
loop
fetch C00 into
	cd_mat_item_solic_compra_w,
	nr_item_solic_compra_w;
EXIT WHEN NOT FOUND; /* apply on C00 */

	open C01;
	loop
	fetch C01 into
		nr_sequencia_doc_tec_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select nextval('solic_compra_item_doc_tec_seq')
		into STRICT   seq_item_doc_tec_w
		;

		insert into solic_compra_item_doc_tec(nr_sequencia,
			 dt_atualizacao,
			 nm_usuario,
			 dt_atualizacao_nrec,
			 nm_usuario_nrec,
			 nr_seq_doc_mat,
			 nr_solic_compra,
			 nr_item_solic_compra)
			values (seq_item_doc_tec_w,
			 clock_timestamp(),
			 nm_usuario_p,
			 clock_timestamp(),
			 nm_usuario_p,
			 nr_sequencia_doc_tec_w,
			 nr_solic_compra_p,
			 nr_item_solic_compra_w);
		end;
	end loop;
	close C01;

end loop;
close C00;
commit;

end;
$body$
