CREATE OR REPLACE PROCEDURE vincular_ficha_ocor_cirurgia ( nr_cirurgia_p bigint, nr_seq_ficha_ocor_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_infeccao_w		timestamp;
nr_seq_sitio_princ_w	bigint;
nr_seq_sitio_espec_w	bigint;


BEGIN

if 	((coalesce(nr_cirurgia_p,0) > 0) and (coalesce(nr_seq_ficha_ocor_p,0) > 0)) then
	begin
	select	dt_infeccao,
		nr_seq_sitio_princ,
		nr_seq_sitio_espec
	into STRICT	dt_infeccao_w,
		nr_seq_sitio_princ_w,
		nr_seq_sitio_espec_w
	from	cih_local_infeccao
	where	nr_sequencia = nr_seq_ficha_ocor_p;

	update	cirurgia
	set	ie_infeccao 		= 'S',
		dt_infeccao 		= dt_infeccao_w,
		nr_seq_sitio_princ 	= nr_seq_sitio_princ_w,
		nr_seq_sitio_espec 	= nr_seq_sitio_espec_w
	where	nr_cirurgia 		= nr_cirurgia_p;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
