CREATE OR REPLACE PROCEDURE vincular_imposto_baixa (nr_seq_imposto_p bigint, nr_seq_baixa_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_imposto_w		double precision;
nr_titulo_w		bigint;
nr_seq_baixa_w		bigint;
nr_lote_contabil_w	bigint;
qt_registro_w		bigint;
/* Projeto Multimoeda - Variáveis */

vl_baixa_estrang_w	double precision;


BEGIN

if (nr_seq_imposto_p IS NOT NULL AND nr_seq_imposto_p::text <> '') and (nr_seq_baixa_p IS NOT NULL AND nr_seq_baixa_p::text <> '') then

	select	nr_titulo,
		vl_imposto,
		nr_seq_baixa
	into STRICT	nr_titulo_w,
		vl_imposto_w,
		nr_seq_baixa_w
	from	titulo_pagar_imposto
	where	nr_sequencia	= nr_seq_imposto_p;

	if (nr_seq_baixa_w IS NOT NULL AND nr_seq_baixa_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265611,'');
		--Já existe uma baixa vinculada a este imposto!
	end if;

	select	count(*)
	into STRICT	qt_registro_w
	from	titulo_pagar_imposto
	where	nr_titulo	= nr_titulo_w
	and	nr_seq_baixa	= nr_seq_baixa_p;

	if (qt_registro_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265614,'');
		--Já existe um imposto vinculado a esta baixa!
	end if;

	update	titulo_pagar_imposto
	set	nr_seq_baixa	= nr_seq_baixa_p
	where	nr_sequencia	= nr_seq_imposto_p;

	update	titulo_pagar_baixa
	set	vl_baixa	= vl_baixa - vl_imposto_w,
		vl_pago		= vl_pago - vl_imposto_w,
		vl_imposto	= coalesce(vl_imposto,0) + vl_imposto_w
	where	nr_titulo	= nr_titulo_w
	and	nr_sequencia	= nr_seq_baixa_p;

	/* Projeto Multimoeda - Verifica se a baixa é em moeda estrangeira, caso positivo atualiza também o valor da baixa em moeda estrangeira. */

	select max(vl_baixa_estrang)
	into STRICT vl_baixa_estrang_w
	from titulo_pagar_baixa
	where nr_titulo = nr_titulo_w
	  and nr_sequencia = nr_seq_baixa_p
	  and coalesce(vl_cotacao,0) <> 0;

	if (coalesce(vl_baixa_estrang_w,0) <> 0) then
		update titulo_pagar_baixa
		set 	vl_baixa_estrang = vl_baixa_estrang - (vl_imposto_w / vl_cotacao),
			vl_complemento = vl_baixa - (vl_baixa_estrang - (vl_imposto_w / vl_cotacao))
		where nr_titulo = nr_titulo_w
		  and nr_sequencia = nr_seq_baixa_p;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
