CREATE OR REPLACE PROCEDURE vincular_indicador_painel ( nr_seq_indicador_p bigint, nr_seq_painel_p bigint, ie_opcao_p text, ie_tipo_indicador_p text, nr_seq_painel_controle_p bigint) AS $body$
DECLARE


/* Opções
	V - vincular
	D - desvincular	*/
qt_registros_w	bigint;


BEGIN

if (ie_opcao_p = 'V') and (nr_seq_indicador_p IS NOT NULL AND nr_seq_indicador_p::text <> '') and (nr_seq_painel_p IS NOT NULL AND nr_seq_painel_p::text <> '') then

	if (nr_seq_indicador_p <> 0) then

		insert into painel_grafico(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_painel,
						nr_seq_grafico,
						ie_tipo_indicador,
						nr_seq_painel_controle)
			values (	nextval('painel_grafico_seq'),
						clock_timestamp(),
						'Tasy',
						clock_timestamp(),
						'Tasy',
						nr_seq_painel_p,
						nr_seq_indicador_p,
						ie_tipo_indicador_p,
						null);

	elsif (nr_seq_painel_controle_p <> 0) then

		insert into painel_grafico(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_painel,
						nr_seq_grafico,
						ie_tipo_indicador,
						nr_seq_painel_controle)
			values (	nextval('painel_grafico_seq'),
						clock_timestamp(),
						'Tasy',
						clock_timestamp(),
						'Tasy',
						nr_seq_painel_p,
						null,
						ie_tipo_indicador_p,
						nr_seq_painel_controle_p);
	end if;

elsif (ie_opcao_p = 'D') then

	if (nr_seq_indicador_p <> 0) then

		select	count(*)
		into STRICT	qt_registros_w
		from	painel_grafico
		where	nr_seq_painel = nr_seq_painel_p
		and	nr_seq_grafico = nr_seq_indicador_p;

		if (coalesce(qt_registros_w,0) > 0) then

			delete from painel_grafico where nr_seq_painel = nr_seq_painel_p and nr_seq_grafico = nr_seq_indicador_p;

		end if;

	else
		select	count(*)
		into STRICT	qt_registros_w
		from	painel_grafico
		where	nr_seq_painel = nr_seq_painel_p
		and	nr_seq_painel_controle = nr_seq_painel_controle_p;

		if (coalesce(qt_registros_w,0) > 0) then

			delete from painel_grafico where nr_seq_painel = nr_seq_painel_p and nr_seq_painel_controle = nr_seq_painel_controle_p;

		end if;
	end if;

end if;

commit;

end;
$body$
