CREATE OR REPLACE PROCEDURE vincular_item_nf_ordem_compra ( nr_sequencia_p bigint, nr_item_nf_p bigint, nr_ordem_compra_p bigint, nr_item_oci_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_local_estoque_w	bigint;
cd_centro_custo_w	bigint;


BEGIN

select	cd_local_estoque,
	cd_centro_custo
into STRICT	cd_local_estoque_w,
	cd_centro_custo_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

update	nota_fiscal_item
set	nr_ordem_compra		= nr_ordem_compra_p,
	nr_item_oci		= nr_item_oci_p,
	cd_local_estoque	= cd_local_estoque_w,
	cd_centro_custo		= cd_centro_custo_w,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_sequencia_p
and	nr_item_nf		= nr_item_nf_p;

CALL gerar_historico_nota_fiscal(
	nr_sequencia_p,
	nm_usuario_p,
	53,
	WHEB_MENSAGEM_PCK.get_texto(297999,'NR_ORDEM_COMPRA_P='|| nr_ordem_compra_p || ';' || 'NR_ITEM_NF_P=' || nr_item_nf_p));

commit;

end;
