CREATE OR REPLACE PROCEDURE vincular_item_ordem_cotacao ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nr_seq_resumo_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_material_w		double precision;
vl_total_item_w		double precision;
vl_unitario_material_w	double precision;
nr_cot_compra_w		bigint;
nr_item_cot_compra_w	bigint;
nr_seq_cot_item_forn_w	bigint;
qt_registro_w		bigint;

BEGIN

if (nr_seq_resumo_p > 0) then

	select	coalesce(vl_unitario_material,0),
		nr_cot_compra,
		nr_item_cot_compra,
		nr_seq_cot_item_forn
	into STRICT	vl_unitario_material_w,
		nr_cot_compra_w,
		nr_item_cot_compra_w,
		nr_seq_cot_item_forn_w
	from	cot_compra_resumo
	where	nr_sequencia = nr_seq_resumo_p;

	select	qt_material
	into STRICT	qt_material_w
	from	ordem_compra_item
	where	nr_ordem_compra		= nr_ordem_compra_p
	and	nr_item_oci		= nr_item_oci_p;

	vl_total_item_w	:= qt_material_w * coalesce(vl_unitario_material_w,0);

	update	ordem_compra_item
	set	nr_cot_compra		= nr_cot_compra_w,
		nr_item_cot_compra	= nr_item_cot_compra_w,
		vl_unitario_material		= vl_unitario_material_w,
		vl_total_item		= vl_total_item_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_ordem_compra		= nr_ordem_compra_p
	and	nr_item_oci		= nr_item_oci_p;

	update	cot_compra_forn_item
	set	nr_ordem_compra	= nr_ordem_compra_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_Sequencia	= nr_seq_cot_item_forn_w;

	select	count(*)
	into STRICT	qt_registro_w
	from	cot_compra_resumo a
	where	a.nr_cot_compra = nr_cot_compra_w
	and not exists (	SELECT	1
		from	ordem_compra_item x
		where	a.nr_cot_compra = x.nr_cot_compra
		and	a.nr_item_cot_compra = x.nr_item_cot_compra);

	if (qt_registro_w = 0) then
		update	cot_compra
		set	dt_geracao_ordem_compra	= clock_timestamp(),
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_cot_compra		= nr_cot_compra_w;
	end if;

	CALL gerar_ordem_compra_hist(	nr_ordem_compra_p,
					'S',
					WHEB_MENSAGEM_PCK.get_texto(298002),
					WHEB_MENSAGEM_PCK.get_texto(298003,'NR_ITEM_OCI_P=' || nr_item_oci_p || ';' ||
									'NR_ITEM_COT_COMPRA_W=' || nr_item_cot_compra_w || ';' ||
									'NR_COT_COMPRA_W=' || nr_cot_compra_w),
					nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
