CREATE OR REPLACE PROCEDURE vincular_link_tele_atendimento ( nr_sequencia_p agenda_consulta.nr_sequencia%TYPE, link_tele_atendimento_p agenda_consulta_adic.ds_url_tele_terc%TYPE ) AS $body$
DECLARE

    existe_agenda_w agenda.ie_sexo_agenda%TYPE;

BEGIN
    SELECT (CASE WHEN (MAX(nr_seq_agenda) IS NOT NULL AND (MAX(nr_seq_agenda))::text <> '') THEN 'S' ELSE 'N' END)
    INTO STRICT existe_agenda_w
    FROM agenda_consulta_adic
    WHERE nr_seq_agenda = nr_sequencia_p;

    IF existe_agenda_w = 'S' THEN
        UPDATE agenda_consulta_adic
        SET ds_url_tele_terc = link_tele_atendimento_p
        WHERE nr_seq_agenda = nr_sequencia_p;
    ELSE
        INSERT INTO agenda_consulta_adic(nr_seq_agenda, ds_url_tele_terc)
        VALUES (nr_sequencia_p, link_tele_atendimento_p);
    END IF;
END;
