SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_material_far_com (cd_codigo_p text, ie_tipo_p text, cd_material_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;
nr_sequencia_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	material_sistema_externo
where	cd_codigo	= cd_codigo_p
and	ie_sistema	= ie_tipo_p;

if (qt_existe_w = 0) then
	begin

	select 	nextval('material_sistema_externo_seq')
	into STRICT	nr_sequencia_w
	;

	insert into MATERIAL_SISTEMA_EXTERNO(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_sistema,
		cd_material,
		cd_codigo,
		cd_referencia) values (nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ie_tipo_p,
				cd_material_p,
				cd_codigo_p,
				'');

	end;
elsif (qt_existe_w > 0) then
	begin

	update	material_sistema_externo
	set	cd_material		= cd_material_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	cd_codigo		= cd_codigo_p
	and	ie_sistema		= ie_tipo_p;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
