SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_medico_laudo_ws ( nr_seq_laudo_P bigint, cd_medico_p text, nr_crm_medico_p text, ds_uf_crm_medico_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


nr_prescricao_w		bigint;
nr_seq_prescricao_w	bigint;
nr_seq_propaci_w		bigint;
nr_seq_proc_interno_w	bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
qt_procedimento_w		bigint;
cd_setor_atendimento_w	bigint;
cd_medico_exec_w		varchar(10);
nr_seq_exame_w		bigint;
ie_lado_w		varchar(15);
dt_prev_execucao_w	timestamp;
nr_atendimento_w		bigint;
dt_entrada_unidade_w	timestamp;
nr_laudo_w        		bigint;
cd_medico_resp_w		varchar(10);
qt_existe_medico_w	bigint;
nr_seq_laudo_w		bigint;
nr_seq_laudo_ant_w	bigint;
nr_seq_laudo_atual_w	bigint;
nr_seq_copia_w		bigint;
qt_existe_w		bigint;


BEGIN

select 	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_laudo_atual_w
from	laudo_paciente
where	nr_sequencia	= nr_seq_laudo_p;

if (nr_seq_laudo_atual_w > 0) then
	
	if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
		select	count(*)
		into STRICT	qt_existe_medico_w
		from	medico
		where	cd_pessoa_fisica	= cd_medico_p;
		
		if (qt_existe_medico_w = 0) then
			select	coalesce(max(cd_pessoa_fisica),'0')
			into STRICT	cd_medico_resp_w
			from	medico
			where	nr_crm	= nr_crm_medico_p
			and	uf_crm	= ds_uf_crm_medico_p;
		else
			cd_medico_resp_w	:= cd_medico_p;
		end if;
	else
		select	coalesce(max(cd_pessoa_fisica),'0')
		into STRICT	cd_medico_resp_w
		from	medico
		where	nr_crm	= nr_crm_medico_p
		and	uf_crm	= ds_uf_crm_medico_p;
	end if;
	
	if (coalesce(cd_medico_resp_w,'0') = '0') then
		ds_erro_p := ds_erro_p || WHEB_MENSAGEM_PCK.get_texto(281895,'CD_MEDICO='||coalesce(to_char(cd_medico_p), '<NULO>') ||';NR_CRM_MEDICO='||coalesce(to_char(nr_crm_medico_p), '<NULO>') ||';DS_UF_CRM_MEDICO='||coalesce(ds_uf_crm_medico_p, '<NULO>') );
	else
		select 	count(*)
		into STRICT	qt_existe_w
		from	LAUDO_PACIENTE_MEDICO
		where	cd_medico 	= cd_medico_resp_w
		and	nr_seq_laudo	= nr_seq_laudo_atual_w;
	
		if (qt_existe_w = 0) then
			insert into LAUDO_PACIENTE_MEDICO(
				nr_sequencia,
				cd_medico,
				nr_seq_laudo,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (nextval('laudo_paciente_medico_seq'),
				cd_medico_resp_w,
				nr_seq_laudo_atual_w,
				clock_timestamp(),
				'Integracao',
				clock_timestamp(),
				'Integracao');
		end if;
	
	end if;		
else
	ds_erro_p := ds_erro_p || WHEB_MENSAGEM_PCK.get_texto(281896,'NR_SEQ_LAUDO_ATUAL='||nr_seq_laudo_atual_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
