CREATE OR REPLACE PROCEDURE vincular_medic_lib_mat_pac ( nr_prescricao_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	lib_material_paciente
where	nr_prescricao	= nr_prescricao_p
and	nr_seq_material	= nr_sequencia_p
and		coalesce(dt_suspenso::text, '') = '';

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then

	update	prescr_material
	set	nr_seq_lib_mat_pac	= nr_sequencia_w
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia		= nr_sequencia_p;

end if;

commit;

end;
