CREATE OR REPLACE PROCEDURE vincular_nc_nf_original ( nr_sequencia_p bigint, nr_seq_nf_orig_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_serie_nf_orig_w	nota_fiscal.cd_serie_nf%type;
nr_num_nf_orig_w        nota_fiscal.nr_nota_fiscal%type;



BEGIN

select	max(cd_serie_nf),
	max(nr_nota_fiscal)
into STRICT	cd_serie_nf_orig_w,
	nr_num_nf_orig_w
from	nota_fiscal
where	nr_sequencia = nr_seq_nf_orig_p;

update	nota_fiscal
set	cd_serie_referencia = cd_serie_nf_orig_w,
	nr_nota_referencia = substr(nr_num_nf_orig_w,1,7),
	nr_sequencia_ref = nr_seq_nf_orig_p
where	nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
