CREATE OR REPLACE PROCEDURE vincular_nf_lote_protocolo ( nr_seq_nf_p bigint, nr_seq_lote_prot_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
qt_existe_w		bigint;
ds_erro_w		varchar(255);
ds_observacao_w		varchar(255);


BEGIN 
 
select	count(*) 
into STRICT	qt_existe_w 
from	nota_fiscal 
where	nr_seq_lote_prot = nr_seq_lote_prot_p 
and	ie_situacao = '1';
 
if (qt_existe_w > 0) and (coalesce(ds_erro_w::text, '') = '') then 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(281651);
end if;
 
select	count(*) 
into STRICT	qt_existe_w 
from	protocolo_convenio 
where	nr_seq_lote_protocolo = nr_seq_lote_prot_p;
 
if (qt_existe_w = 0) and (coalesce(ds_erro_w::text, '') = '') then 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(281652);
end if;
 
if (coalesce(ds_erro_w::text, '') = '') then 
	begin 
 
	update	nota_fiscal 
	set	nr_seq_lote_prot = nr_seq_lote_prot_p, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where	nr_sequencia = nr_seq_nf_p;
 
	ds_observacao_w	:= WHEB_MENSAGEM_PCK.get_texto(281653) || to_char(nr_seq_lote_prot_p) || WHEB_MENSAGEM_PCK.get_texto(281654);
 
	insert into nota_fiscal_hist( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_nota, 
		cd_evento, 
		ds_historico) values ( 
			nextval('nota_fiscal_hist_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_nf_p, 
			'47', 
			ds_observacao_w);
 
	end;
else 
	ds_erro_p := ds_erro_w;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
