CREATE OR REPLACE PROCEDURE vincular_permissao_agenda (cd_agenda_p bigint, cd_medico_prop_p text, cd_pessoa_fisica_p text) AS $body$
DECLARE

 
nr_sequencia_w	bigint;

c01 CURSOR FOR 
SELECT	coalesce(b.nr_sequencia,0) 
from	agenda a, 
	med_permissao b 
where	a.cd_tipo_agenda	= 3 
and	a.cd_pessoa_fisica	= b.cd_medico_prop 
and	b.cd_medico_prop	= cd_medico_prop_p 
and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	coalesce(b.cd_agenda::text, '') = '' 
order by 
	b.nr_sequencia;


BEGIN 
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (cd_medico_prop_p IS NOT NULL AND cd_medico_prop_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	open c01;
	loop 
	fetch c01 into nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		if (nr_sequencia_w > 0) then 
			update	med_permissao 
			set	cd_agenda	= cd_agenda_p 
			where	nr_sequencia	= nr_sequencia_w;
		end if;
		end;
	end loop;
	close c01;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
