CREATE OR REPLACE PROCEDURE vincular_problema_encam (ds_lista_problema_p text, nr_seq_encaminhamento_p bigint ) AS $body$
DECLARE


	ds_lista_problema_w	varchar(1000);
	nr_pos_virgula_w	integer;
	nr_seq_problema_w	lista_problema_pac.nr_sequencia%type;

BEGIN

	if (ds_lista_problema_p IS NOT NULL AND ds_lista_problema_p::text <> '' AND nr_seq_encaminhamento_p IS NOT NULL AND nr_seq_encaminhamento_p::text <> '') then

		ds_lista_problema_w := ds_lista_problema_p;

		if (substr(ds_lista_problema_w, length(ds_lista_problema_w)) <> ',') then
			ds_lista_problema_w := ds_lista_problema_w ||',';
		end if;

		while(ds_lista_problema_w IS NOT NULL AND ds_lista_problema_w::text <> '') loop
			begin

				nr_pos_virgula_w := position(',' in ds_lista_problema_w);

				if (nr_pos_virgula_w <> 0) then
					nr_seq_problema_w		:= substr(ds_lista_problema_w, 1, (nr_pos_virgula_w - 1));
					ds_lista_problema_w	:= substr(ds_lista_problema_w, (nr_pos_virgula_w + 1), length(ds_lista_problema_w));
				end if;

				if (nr_seq_problema_w IS NOT NULL AND nr_seq_problema_w::text <> '') then

					insert into atend_encaminhamento_probl(
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_encaminhamento,
						nr_seq_problema
					) values (
						nextval('atend_encaminhamento_probl_seq'),
						clock_timestamp(),
						wheb_usuario_pck.get_nm_usuario,
						clock_timestamp(),
						wheb_usuario_pck.get_nm_usuario,
						nr_seq_encaminhamento_p,
						nr_seq_problema_w
					);


				end if;

			end;
		end loop;

	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
