SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_problema (nr_sequencia_p bigint, nr_seq_problema_sup_p bigint, ie_opcao_p text) AS $body$
DECLARE


dt_inicio_atual_w timestamp;
dt_inicio_sup_w timestamp;						
vinc_count_v bigint;
nr_seq_vinc_pai_v bigint;


BEGIN

if (ie_opcao_p = 'V') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_seq_problema_sup_p IS NOT NULL AND nr_seq_problema_sup_p::text <> '') then
		select count(*) into STRICT vinc_count_v from lista_problema_pac where nr_seq_vinc_pai = nr_sequencia_p;
		select nr_seq_vinc_pai into STRICT nr_seq_vinc_pai_v from lista_problema_pac where nr_sequencia = nr_seq_problema_sup_p;
		
		if (vinc_count_v <> 0) then
			update	lista_problema_pac
			set	    nr_seq_vinc_pai     = nr_seq_problema_sup_p
			where	nr_seq_vinc_pai	    = nr_sequencia_p;
			
			update	lista_problema_pac
			set	    nr_seq_problema_sup     = nr_seq_problema_sup_p,
					nr_seq_vinc_pai 	    = coalesce(nr_seq_vinc_pai_v, nr_seq_problema_sup_p)
			where	nr_sequencia	        = nr_sequencia_p;
			commit;
			
		else
			update  lista_problema_pac
			set	    nr_seq_problema_sup     = nr_seq_problema_sup_p,
					nr_seq_vinc_pai 	    = coalesce(nr_seq_vinc_pai_v, nr_seq_problema_sup_p)
			where	nr_sequencia	        = nr_sequencia_p;
			commit;
		end if;
			
elsif (ie_opcao_p = 'D') then
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

		update	lista_problema_pac
		set	nr_seq_problema_sup  = NULL
		where	nr_sequencia	    = nr_sequencia_p;
		commit;
	end if;

elsif (ie_opcao_p = 'A') then
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_seq_problema_sup_p IS NOT NULL AND nr_seq_problema_sup_p::text <> '')	then

		select dt_inicio
		into STRICT dt_inicio_atual_w
		from lista_problema_pac
		where nr_sequencia = nr_sequencia_p;
		
		select dt_inicio
		into STRICT dt_inicio_sup_w
		from lista_problema_pac
		where nr_sequencia = nr_seq_problema_sup_p;
		
		if (dt_inicio_atual_w < dt_inicio_sup_w) then
			
			update	lista_problema_pac
			set	nr_seq_problema_sup = nr_sequencia_p
			where	nr_sequencia	    = nr_seq_problema_sup_p;
			commit;
			
		elsif (dt_inicio_atual_w >= dt_inicio_sup_w) then
		
			update	lista_problema_pac
			set	nr_seq_problema_sup = nr_seq_problema_sup_p
			where	nr_sequencia	    = nr_sequencia_p;
			commit;			
			
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
