CREATE OR REPLACE PROCEDURE vincular_procedimento_cirurgia ( nr_sequencia_p bigint, nr_cirurgia_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
 
cd_pessoa_proc_w		varchar(10);
nr_atend_proc_w			bigint;
cd_pessoa_cirurgia_w		varchar(10);
nr_atend_cirurgia_w		bigint;
ds_erro_w			varchar(255) := '';

 

BEGIN 
 
/* Obter dados do procedimento */
 
begin 
select	c.cd_pessoa_fisica, 
	c.nr_atendimento 
into STRICT	cd_pessoa_proc_w, 
	nr_atend_proc_w 
from	atendimento_paciente	c, 
	conta_paciente		b, 
	procedimento_paciente	a 
where	a.nr_sequencia		= nr_sequencia_p 
and	a.nr_interno_conta	= b.nr_interno_conta 
and	b.nr_atendimento	= c.nr_atendimento;
exception 
	when others then 
	cd_pessoa_proc_w	:= '';
	nr_atend_proc_w		:= 0;
end;
 
/* Obter dados da cirurgia */
 
begin 
select	cd_pessoa_fisica, 
	nr_atendimento 
into STRICT	cd_pessoa_cirurgia_w, 
	nr_atend_cirurgia_w 
from	cirurgia 
where	nr_cirurgia		= nr_cirurgia_p;
exception 
	when others then 
	cd_pessoa_cirurgia_w	:= '';
	nr_atend_cirurgia_w	:= 0;
end;
 
if (cd_pessoa_proc_w = cd_pessoa_cirurgia_w) and (nr_atend_proc_w = nr_atend_cirurgia_w) then 
	begin 
	update	procedimento_paciente 
	set	nr_cirurgia	= nr_cirurgia_p 
	where	nr_sequencia	= nr_sequencia_p;
 
	commit;
	ds_erro_w	:= wheb_mensagem_pck.get_texto(279949);
	end;
else 
	ds_erro_w	:= wheb_mensagem_pck.get_texto(279950);
end if;
 
ds_erro_p		:= ds_erro_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
