CREATE OR REPLACE PROCEDURE vincular_protocolo_perda_fat ( cd_estabelecimento_p bigint, nr_seq_perda_p bigint, nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_registro_w				bigint;
cd_convenio_protocolo_w			integer;
cd_convenio_perda_w			integer;

 
c01 CURSOR FOR 
SELECT	a.nr_interno_conta 
from	conta_paciente a 
where	a.nr_seq_protocolo	= nr_seq_protocolo_p 
and	a.cd_estabelecimento	= cd_estabelecimento_p 
and	a.ie_status_acerto	= 2 
and	not exists (	SELECT	1 
			from	pre_fatur_perda_conta y 
			where	y.nr_interno_conta	= a.nr_interno_conta);

vet01	C01%RowType;


BEGIN 
 
select	max(cd_convenio) 
into STRICT	cd_convenio_perda_w 
from	pre_fatur_perda 
where	nr_sequencia	= nr_seq_perda_p;
	 
if (coalesce(cd_convenio_perda_w, 0) <> 0) then 
	 
	select	max(cd_convenio) 
	into STRICT	cd_convenio_protocolo_w 
	from	protocolo_convenio 
	where	nr_seq_protocolo = nr_seq_protocolo_p;
	 
	if (coalesce(cd_convenio_protocolo_w, 0) <> 0) and (cd_convenio_protocolo_w <> cd_convenio_perda_w) then 
		 
		--R.aise_application_error(-20011, 'Este protocolo não é deste convênio!#@#@'); 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263640);
	end if;
	 
end if;
 
 
select	count(*) 
into STRICT	qt_registro_w 
from	protocolo_convenio 
where	nr_seq_protocolo	= nr_seq_protocolo_p;
 
if (qt_registro_w = 0) then 
	--R.aise_application_error(-20011,'Este protocolo não existe!#@#@'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263641);
end if;
 
open C01;
loop 
fetch C01 into	 
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL vincular_conta_perda_fat(cd_estabelecimento_p, nr_seq_perda_p, vet01.nr_interno_conta, nm_usuario_p);
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
