CREATE OR REPLACE PROCEDURE vincular_queixa_problema (nr_seq_problema_p bigint, nr_seq_ciap_p bigint, cd_ciap_p text ) AS $body$
DECLARE


	nm_usuario_p usuario.nm_usuario%type;


BEGIN
	if (nr_seq_problema_p IS NOT NULL AND nr_seq_problema_p::text <> '' AND nr_seq_ciap_p IS NOT NULL AND nr_seq_ciap_p::text <> '') then

			nm_usuario_p := wheb_usuario_pck.get_nm_usuario;

			insert into lista_problema_pac_item(	nr_sequencia,
											dt_atualizacao,
											dt_atualizacao_nrec,
											nm_usuario,
											nm_usuario_nrec,
											nr_seq_problema,
											nr_seq_registro,
											ie_tipo_registro,
											nm_tabela,
											ie_tipo_problema)
								values (		nextval('lista_problema_pac_item_seq'),
											clock_timestamp(),
											clock_timestamp(),
											nm_usuario_p,
											nm_usuario_p,
											nr_seq_problema_p,
											nr_seq_ciap_p,
											'CP',
											'CIAP_ATENDIMENTO',
											cd_ciap_p);  -- CÃ³digo do ciap
											commit;


	end if;

end;
