CREATE OR REPLACE PROCEDURE vincular_receb_lote_hist (nr_seq_analise_p bigint, nr_seq_lote_p bigint, nr_seq_receb_p bigint, vl_a_vincular_vinc_p bigint, vl_saldo_vinc_p bigint, nm_usuario_p text, ie_acao_p text) AS $body$
DECLARE


/*
V	- Vincular
D	- Desvincular
*/
nr_adiantamento_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_adiantamento
from	convenio_receb a,
	lote_audit_hist_receb b
where	b.nr_seq_analise	= nr_seq_analise_p
and	b.nr_seq_receb		= a.nr_sequencia;


BEGIN

if (ie_acao_p = 'V') then

	insert into lote_audit_hist_receb(dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nm_usuario,
		nr_seq_lote,
		nr_seq_analise,
		nr_seq_receb,
		nr_sequencia,
		vl_vinculado)
	values (clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_lote_p,
		nr_seq_analise_p,
		nr_seq_receb_p,
		nextval('lote_audit_hist_receb_seq'),
		vl_a_vincular_vinc_p);

	select	a.nr_adiantamento
	into STRICT	nr_adiantamento_w
	from	convenio_receb a
	where	a.nr_sequencia	= nr_seq_receb_p;

	if (coalesce(nr_adiantamento_w,0) > 0) then
		CALL atualizar_saldo_adiantamento(nr_adiantamento_w, nm_usuario_p, null);
	end if;

	if	((vl_saldo_vinc_p - vl_a_vincular_vinc_p) = 0) then
		update	convenio_receb
		set	ie_status	= 'T'
		where	nr_sequencia	= nr_seq_receb_p;
	else
		update	convenio_receb
		set	ie_status	= 'P'
		where	nr_sequencia	= nr_seq_receb_p;
	end if;

elsif (ie_acao_p = 'D') then

	delete	from lote_audit_hist_receb
	where	nr_seq_analise	= nr_seq_analise_p;

	update	lote_audit_hist
	set	dt_vinculacao	 = NULL,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_analise_p;

	open c01;
	loop
	fetch c01 into
		nr_adiantamento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		CALL atualizar_saldo_adiantamento(nr_adiantamento_w, nm_usuario_p, null);

	end loop;
	close c01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
