SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_saida_corresp ( nr_sequencia_p bigint, nr_seq_saida_corresp_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w		bigint;
qt_saida_real_w		double precision;


BEGIN

update	rop_lavanderia
set	nr_seq_saida_corresp = nr_seq_saida_corresp_p
where	nr_sequencia = nr_sequencia_p;

select	count(*)
into STRICT	qt_registro_w
from	rop_lavanderia_inf_peso
where	nr_seq_lavanderia = nr_sequencia_p;

select	coalesce(sum(qt_saida_real),0)
into STRICT	qt_saida_real_w
from	rop_lavanderia_inf_peso
where	nr_seq_lavanderia = nr_seq_saida_corresp_p;

if (qt_registro_w = 0) and (qt_saida_real_w > 0) then

	insert into rop_lavanderia_inf_peso(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_lavanderia,
		qt_saida_real)
	values (	nextval('rop_lavanderia_inf_peso_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		qt_saida_real_w);
end if;

if (qt_registro_w > 0) and (qt_saida_real_w > 0) then

	update	rop_lavanderia_inf_peso
	set	qt_saida_real = qt_saida_real_w
	where	nr_seq_lavanderia = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
