SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_senha_agendamento ( nr_seq_senha_p bigint, nr_seq_agenda_p bigint, ie_tipo_agenda_p text) AS $body$
DECLARE



cd_pessoa_fisica_w			varchar(60);	
ie_status_padrao_w			varchar(15) := null;
nr_atendimeto_w             atendimento_paciente.nr_atendimento%type;
		

BEGIN

if (ie_tipo_agenda_p = 'E') then
	begin
	if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
		begin
				
		select	CD_PESSOA_FISICA,
		        NR_ATENDIMENTO
		into STRICT	cd_pessoa_fisica_w,
		        nr_atendimeto_w
		from	agenda_paciente
		where	nr_sequencia		= nr_seq_agenda_p;	
	
		
		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
			begin
			update	agenda_paciente
			set		nr_seq_pac_senha_fila	= 	nr_seq_senha_p
			where	nr_sequencia		= nr_seq_agenda_p
			and		cd_pessoa_fisica	=	cd_pessoa_fisica_w;
			
			if (obter_funcao_ativa = 871) then
				ie_status_padrao_w := obter_param_usuario(871, 778, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_status_padrao_w);
				if (ie_status_padrao_w is  not null) then
					update	agenda_paciente
					set		ie_status_agenda	= ie_status_padrao_w
					where	nr_sequencia		= nr_seq_agenda_p
					and		cd_pessoa_fisica	=cd_pessoa_fisica_w;
				end if;
			end if;
			
			end;
		end if;
		exception when too_many_rows or no_data_found then
		    cd_pessoa_fisica_w := null;
		    nr_atendimeto_w := null;
		end;
	end if;
	end;
elsif (ie_tipo_agenda_p = 'S') then
	begin
	if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
		begin
		select	CD_PESSOA_FISICA,
		        NR_ATENDIMENTO
		into STRICT	cd_pessoa_fisica_w,
		        nr_atendimeto_w
		from	agenda_consulta
		where	nr_sequencia		= nr_seq_agenda_p;	
		
		
		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
			begin
			update	agenda_consulta
			set	nr_seq_pac_senha_fila	= 	nr_seq_senha_p
			where	nr_sequencia		= nr_seq_agenda_p
			and	cd_pessoa_fisica	=	cd_pessoa_fisica_w;
			end;
		end if;
		exception when too_many_rows or no_data_found then
		    cd_pessoa_fisica_w := null;
		    nr_atendimeto_w := null;
		end;
	end if;	
	end;
end if;

if (nr_atendimeto_w IS NOT NULL AND nr_atendimeto_w::text <> '') then
    update atendimento_paciente
    set NR_SEQ_PAC_SENHA_FILA = nr_seq_senha_p
    where nr_atendimento = nr_atendimeto_w;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
