SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_titulo_pagar_contrato ( nr_seq_contrato_p bigint, nr_titulo_pagar_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;
nr_seq_contrato_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	titulo_pagar
where	nr_titulo = nr_titulo_pagar_p;

if (qt_registros_w = 0) then
	CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(237297);
else

	select	coalesce(nr_seq_contrato,0)
	into STRICT	nr_seq_contrato_w
	from	titulo_pagar
	where	nr_titulo = nr_titulo_pagar_p;

	if (nr_seq_contrato_w > 0) then
		CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(237304);
	else
		update	titulo_pagar
		set	nr_seq_contrato = nr_seq_contrato_p
		where	nr_titulo = nr_titulo_pagar_p;

		CALL atualiza_hist_lote_pagar(
			nm_usuario_p,
			--'Foi vinculado manualmente o contrato ' || nr_seq_contrato_p || ' com esse título a pagar através da opção Vincular título a pagar que existe na função Controle de Contratos.',
			wheb_mensagem_pck.get_texto(303990,'NR_SEQ_CONTRATO_P='||nr_seq_contrato_p),
			nr_titulo_pagar_p,'V');
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE vincular_titulo_pagar_contrato ( nr_seq_contrato_p bigint, nr_titulo_pagar_p bigint, nm_usuario_p text) FROM PUBLIC;

