SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_titulo_receber_nf ( nr_sequencia_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_nota_fiscal_w	varchar(255);
ie_copiar_tributo_w	varchar(1);
cd_estabelecimento_w	smallint;


BEGIN


select	max(a.nr_nota_fiscal),
	max(a.cd_estabelecimento)
into STRICT	nr_nota_fiscal_w,
	cd_estabelecimento_w
from	nota_fiscal a
where	a.nr_sequencia = nr_sequencia_p;

update	titulo_receber
set	nr_nota_fiscal 	= nr_nota_fiscal_w,
	nr_seq_nf_saida = nr_sequencia_p
where 	nr_titulo 	= nr_titulo_p
and	coalesce(nr_nota_fiscal::text, '') = ''
and	coalesce(nr_seq_nf_saida::text, '') = '';

ie_copiar_tributo_w := obter_param_usuario(40, 301, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_copiar_tributo_w);

if (ie_copiar_tributo_w	= 'S') then

	insert	into titulo_receber_trib(cd_tributo,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_tributo,
		ie_origem_tributo,
		nm_usuario,
		nm_usuario_nrec,
		nr_sequencia,
		nr_titulo,
		tx_tributo,
		vl_base_adic,
		vl_base_calculo,
		vl_base_nao_retido,
		vl_trib_adic,
		vl_trib_nao_retido,
		vl_tributo)
	SELECT	a.cd_tributo,
		clock_timestamp(),
		clock_timestamp(),
		clock_timestamp(),
		'C',
		nm_usuario_p,
		nm_usuario_p,
		nextval('titulo_receber_trib_seq'),
		nr_titulo_p,
		a.tx_tributo,
		a.vl_base_adic,
		a.vl_base_calculo,
		a.vl_base_nao_retido,
		a.vl_trib_adic,
		a.vl_trib_nao_retido,
		a.vl_tributo
	from	nota_fiscal_trib a
	where	a.nr_sequencia	= nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
