CREATE OR REPLACE PROCEDURE vincular_tit_pagar_barras ( nr_seq_escrit_p bigint, nr_seq_banco_escrit_barras_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_bloqueto_w	varchar(100);


BEGIN

if (nr_seq_banco_escrit_barras_p IS NOT NULL AND nr_seq_banco_escrit_barras_p::text <> '') then

	select	substr(a.cd_barras,1,100)
	into STRICT	nr_bloqueto_w
	from	banco_escrit_barras a
	where	a.nr_sequencia	= nr_seq_banco_escrit_barras_p;

	CALL validar_se_barras_vinc_titulo(nr_bloqueto_w,nr_titulo_p);

	update	banco_escrit_barras
	set	nr_titulo	= nr_titulo_p
	where	nr_sequencia	= nr_seq_banco_escrit_barras_p;

	update	titulo_pagar
	set	nr_bloqueto	= nr_bloqueto_w,
		ie_bloqueto	= 'S'
	where	nr_titulo	= nr_titulo_p;

	CALL definir_banco_tit_escritural('CP',nr_titulo_p,nr_seq_escrit_p,nm_usuario_p,'N');

	commit;

end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
