SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_tit_rec_nf ( nr_titulo_p bigint, nr_seq_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_nota_fiscal_w	varchar(255);
nr_seq_nf_saida_w	bigint;
nr_interno_conta_w	bigint;
nr_seq_protocolo_w	bigint;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (nr_seq_nf_p IS NOT NULL AND nr_seq_nf_p::text <> '') then

	select	max(nr_nota_fiscal),
		max(nr_interno_conta),
		max(nr_seq_protocolo)
	into STRICT	nr_nota_fiscal_w,
		nr_interno_conta_w,
		nr_seq_protocolo_w
	from	nota_fiscal
	where	nr_sequencia	= nr_seq_nf_p;

	select	nr_seq_nf_saida
	into STRICT	nr_seq_nf_saida_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_p;

	if (nr_seq_nf_saida_w IS NOT NULL AND nr_seq_nf_saida_w::text <> '') then
		/* O título já possui nota fiscal associada */

		CALL wheb_mensagem_pck.exibir_mensagem_abort(262123);
	end if;

	update	titulo_receber
	set	nr_seq_nf_saida		= nr_seq_nf_p,
		nr_nota_fiscal		= coalesce(nr_nota_fiscal,nr_nota_fiscal_w),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nr_interno_conta	= nr_interno_conta_w,
		nr_seq_protocolo	= nr_seq_protocolo_w
	where	nr_titulo		= nr_titulo_p;

	insert	into	nota_fiscal_hist(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		cd_evento,
		nr_seq_nota,
		ds_historico)
	values (nextval('nota_fiscal_hist_seq'),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		23,
		nr_seq_nf_p,
		wheb_mensagem_pck.get_texto(303946,'NR_TITULO=' || nr_titulo_p));
		--'Título ' || nr_titulo_p || ' vinculado manualmente após a geração da nota.');
else
	/* Faltam informações para o vínculo ser efetuado */

	CALL wheb_mensagem_pck.exibir_mensagem_abort(262124);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
