SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincular_tit_trib_repasse_nf (nr_seq_nota_fiscal_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_titulo_tributo_w		bigint;
nr_seq_tributo_w		bigint;
nr_titulo_w			bigint;
nr_repasse_terceiro_w		bigint;
ds_titulo_tributo_w		varchar(4000);
ds_observacao_w			nota_fiscal.ds_observacao%type;

c01 CURSOR FOR
SELECT	nr_repasse_terceiro
from	repasse_nota_fiscal
where	nr_seq_nota_fiscal	= nr_seq_nota_fiscal_p;

c02 CURSOR FOR
SELECT	nr_titulo
from	repasse_terceiro_venc
where	nr_repasse_terceiro	= nr_repasse_terceiro_w;

c03 CURSOR FOR
SELECT	nr_sequencia
from	titulo_pagar_imposto
where	nr_titulo		= nr_titulo_w;

c04 CURSOR FOR
SELECT	nr_titulo
from	titulo_pagar
where	nr_seq_tributo		= nr_seq_tributo_w;


BEGIN

ds_titulo_tributo_w	:= '';
ds_observacao_w 	:= '';

select	ds_observacao
into STRICT 	ds_observacao_w
from	nota_fiscal
where	nr_sequencia = nr_seq_nota_fiscal_p;

open c01;
loop
fetch c01 into
	nr_repasse_terceiro_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	open c02;
	loop
	fetch c02 into
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

		open c03;
		loop
		fetch c03 into
			nr_seq_tributo_w;
		EXIT WHEN NOT FOUND; /* apply on c03 */

			open c04;
			loop
			fetch c04 into
				nr_titulo_tributo_w;
			EXIT WHEN NOT FOUND; /* apply on c04 */

				update	titulo_pagar
				set	nr_seq_nota_fiscal	= nr_seq_nota_fiscal_p
				where	coalesce(nr_seq_nota_fiscal::text, '') = ''
				and	nr_titulo		= nr_titulo_tributo_w;
				
				if (length(ds_titulo_tributo_w) + length(nr_titulo_tributo_w) + length(ds_observacao_w) < 4000) then
					ds_titulo_tributo_w	:= nr_titulo_tributo_w || ', ' || ds_titulo_tributo_w;
					ds_observacao_w		:= ds_observacao_w || chr(13) || chr(10) || wheb_mensagem_pck.get_texto(303132,'DS_TITULO_TRIBUTO_W=' || ds_titulo_tributo_w);
				end if;	

			end loop;
			close c04;

		end loop;
		close c03;

	end loop;
	close c02;


end loop;
close c01;

if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
	update	nota_fiscal
	set	ds_observacao	= ds_observacao_w
	where	nr_sequencia	= nr_seq_nota_fiscal_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
