CREATE OR REPLACE PROCEDURE vincular_transcr_prescr ( nr_transcricao_p bigint, nr_prescricao_p bigint, ie_vinculo_realizado_p INOUT text, nr_prescr_transcr_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE


nr_prescr_transcr_w	bigint;


BEGIN
ie_vinculo_realizado_p	:= 'N';
nr_prescr_transcr_p	:= 0;
if (nr_transcricao_p IS NOT NULL AND nr_transcricao_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	coalesce(max(nr_prescricao),0)
	into STRICT	nr_prescr_transcr_w
	from	prescr_medica
	where	nr_seq_transcricao = nr_transcricao_p;

	if (nr_prescr_transcr_w = 0) then
		update	prescr_medica
		set	nr_seq_transcricao	= nr_transcricao_p,
			nm_usuario	= nm_usuario_p
		where	nr_prescricao	= nr_prescricao_p;

		ie_vinculo_realizado_p	:= 'S';
	else
		ie_vinculo_realizado_p	:= 'N';
		nr_prescr_transcr_p		:= nr_prescr_transcr_w;
	end if;

else
	ie_vinculo_realizado_p	:= 'N';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
