SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vincula_atendimento_senha (nr_seq_pac_senha_fila_p bigint, cd_pessoa_fisica_p bigint, cd_estabelecimento_p bigint, ie_acao_p text, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

						 
/* 
ie_acao_p: 
V - Vincular senha 
D - Desvincular senha 
*/
 
						 
ie_inicia_atend_vinc_senha_w	varchar(1);
ie_vinc_descv_senha_agenda_w	varchar(1);
nr_seq_pac_senha_fila_w			atendimento_paciente.nr_seq_pac_senha_fila%type;


BEGIN 
 
ie_inicia_atend_vinc_senha_w := obter_param_usuario(10021, 68, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_inicia_atend_vinc_senha_w);
ie_vinc_descv_senha_agenda_w := obter_param_usuario(916, 1180, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_vinc_descv_senha_agenda_w);
 
 
if (nr_seq_pac_senha_fila_p IS NOT NULL AND nr_seq_pac_senha_fila_p::text <> '') and (ie_acao_p = 'V')then 
	update	paciente_senha_fila 
	set	dt_vinculacao_senha 	= clock_timestamp(), 
		nm_usuario				= nm_usuario_p, 
		cd_pessoa_fisica 		= cd_pessoa_fisica_p 
	where	nr_sequencia 		= nr_seq_pac_senha_fila_p;
	 
	if (coalesce(ie_vinc_descv_senha_agenda_w, 'N') = 'S') then 
		-- AGENDA DE SERVIÇOS e AGENDA DE CONSULTAS 
		update agenda_consulta 
		set	nr_seq_pac_senha_fila = nr_seq_pac_senha_fila_p 
		where nr_atendimento = nr_atendimento_p;
		-- AGENDA DE EXAMES e AGENDA CIRÚRGICA 
		update agenda_paciente 
		set	nr_seq_pac_senha_fila = nr_seq_pac_senha_fila_p 
		where nr_atendimento = nr_atendimento_p;
		-- AGENDA DE QUIMIOTERAPIA 
		update agenda_quimio 
		set	nr_seq_pac_senha_fila = nr_seq_pac_senha_fila_p 
		where nr_atendimento = nr_atendimento_p;
	end if;
	 
	if (ie_inicia_atend_vinc_senha_w = 'S') then 
		CALL iniciar_finalizar_atend_senha(nr_seq_pac_senha_fila_p, 'I', nm_usuario_p, cd_estabelecimento_p, 'N');
	end if;
	 
	--Atualizar o histórico de senhas vinculadas 
	insert into senhas_atendimento(	 
			nr_sequencia,        
			nr_seq_pac_senha_fila, 
			nr_atendimento, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec 
		) values (	nextval('senhas_atendimento_seq'), 
			nr_seq_pac_senha_fila_p, 
			nr_atendimento_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p);
	 
	 
elsif (ie_acao_p = 'D') then 
	begin 
	 
	update	paciente_senha_fila 
	set	dt_vinculacao_senha 	 = NULL, 
		nm_usuario				= nm_usuario_p, 
		cd_pessoa_fisica 		 = NULL 
	where	nr_sequencia 		= nr_seq_pac_senha_fila_p;
	 
	delete FROM senhas_atendimento where nr_seq_pac_senha_fila = nr_seq_pac_senha_fila_p;
	 
	select	max(nr_seq_pac_senha_fila) 
	into STRICT	nr_seq_pac_senha_fila_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	--Desvincular a senha na EUP 
	if (nr_seq_pac_senha_fila_w = nr_seq_pac_senha_fila_p)then 
		update	atendimento_paciente 
		set		ds_senha_qmatic  = NULL, 
			nr_seq_pac_senha_fila  = NULL 
		where 	nr_atendimento	= nr_atendimento_p;
		 
		if (coalesce(ie_vinc_descv_senha_agenda_w, 'N') = 'S') then 
			-- AGENDA DE SERVIÇOS e AGENDA DE CONSULTAS 
			update agenda_consulta 
			set	nr_seq_pac_senha_fila  = NULL 
			where nr_atendimento = nr_atendimento_p;
			-- AGENDA DE EXAMES e AGENDA CIRÚRGICA 
			update agenda_paciente 
			set	nr_seq_pac_senha_fila  = NULL 
			where nr_atendimento = nr_atendimento_p;
			-- AGENDA DE QUIMIOTERAPIA 
			update agenda_quimio 
			set	nr_seq_pac_senha_fila  = NULL 
			where nr_atendimento = nr_atendimento_p;
		end if;		
	end if;	
	 
	commit;
	CALL iniciar_finalizar_atend_senha(nr_seq_pac_senha_fila_p, 'DIF', nm_usuario_p, cd_estabelecimento_p, 'N');
	 
	exception 
	when others then 
		null;
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE vincula_atendimento_senha (nr_seq_pac_senha_fila_p bigint, cd_pessoa_fisica_p bigint, cd_estabelecimento_p bigint, ie_acao_p text, nr_atendimento_p bigint, nm_usuario_p text) FROM PUBLIC;

