CREATE OR REPLACE PROCEDURE vincula_autorizacao_atend (nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_seq_gestao_p bigint, cd_convenio_p bigint) AS $body$
DECLARE


dt_inicio_vigencia_w	timestamp;
nr_sequencia_w			bigint;


BEGIN


SELECT 	MAX(nr_sequencia)
INTO STRICT	nr_sequencia_w
FROM 	autorizacao_convenio
WHERE   clock_timestamp() BETWEEN coalesce(dt_inicio_vigencia,(clock_timestamp() - interval '1 days'/1440)) AND coalesce(dt_fim_vigencia,(clock_timestamp() + interval '1 days'/1440))
AND 	cd_pessoa_fisica = cd_pessoa_fisica_p
AND 	coalesce(nr_atendimento::text, '') = ''
AND	nr_seq_gestao = nr_seq_gestao_p
AND	cd_convenio = cd_convenio_p;

IF (coalesce(nr_sequencia_w,0) > 0) THEN

	UPDATE 	autorizacao_convenio
	SET 	nr_atendimento = nr_atendimento_p
	WHERE 	nr_sequencia = nr_sequencia_w;

END IF;

COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
