CREATE OR REPLACE PROCEDURE vinc_agenda_pac_lista_espera ( nr_seq_agenda_p bigint, nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE



dt_prevista_internacao_w     	timestamp;


BEGIN
begin

if ((coalesce(nr_seq_agenda_p,0) > 0) and (coalesce(nr_sequencia_p,0) > 0)) then

	select  max(dt_prevista_internacao)
	into STRICT	dt_prevista_internacao_w
	from    paciente_espera
	where	nr_sequencia =  nr_sequencia_p;

	update agenda_paciente
	set    DT_CHEGADA_PREV = dt_prevista_internacao_w
	where  nr_sequencia    = nr_seq_agenda_p;

	update	paciente_espera
	set	nr_seq_agenda = nr_seq_agenda_p
	where	nr_sequencia =  nr_sequencia_p;

	commit;

end if;

exception
when others then
	null;
end;

end;
