SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vinc_automaticamente_atend_eup (nr_seq_agenda_p bigint, nr_atendimento_p bigint) AS $body$
DECLARE

 
 
ie_gera_hist_vinc_w	varchar(1);
nm_paciente_w		varchar(240);
dt_agenda_w		varchar(30);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_seq_proc_interno_w	bigint;
ds_historico_w		varchar(4000);
cd_pessoa_atendimento_w varchar(10);


BEGIN 
 
select 	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_atendimento_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
ie_gera_hist_vinc_w := obter_param_usuario(916, 502, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_gera_hist_vinc_w);
 
if (ie_gera_hist_vinc_w = 'S') then 
	begin 
	select	substr(CASE WHEN coalesce(max(cd_pessoa_fisica)::text, '') = '' THEN max(nm_paciente)  ELSE obter_nome_pf(max(cd_pessoa_fisica)) END ,1,240), 
		to_char(max(dt_agenda),'dd/mm/yyyy') || ' ' || to_char(max(hr_inicio),'hh24:mi:ss'), 
		max(cd_procedimento), 
		max(ie_origem_proced), 
		max(nr_seq_proc_interno) 
	into STRICT	nm_paciente_w, 
		dt_agenda_w, 
		cd_procedimento_w, 
		ie_origem_proced_w, 
		nr_seq_proc_interno_w 
	from	agenda_paciente	 
	where	nr_sequencia	= nr_seq_agenda_p;
		 
		 
			 
	ds_historico_w:= substr(upper(wheb_mensagem_pck.get_texto(798469)) || ' ' || chr(13) || 
			wheb_mensagem_pck.get_texto(798496) || ': '|| nm_paciente_w || chr(13) || 
			wheb_mensagem_pck.get_texto(798471) || ': ' || obter_nome_pf(cd_pessoa_atendimento_w) || chr(13) || 
			wheb_mensagem_pck.get_texto(791662) || ' ' || nr_atendimento_p || chr(13) || 
			wheb_mensagem_pck.get_texto(798474) || ': ' || dt_agenda_w || chr(13) || 
			wheb_mensagem_pck.get_texto(795138) || ': '|| substr(obter_exame_agenda(cd_procedimento_w, ie_origem_proced_w, nr_seq_proc_interno_w),1,240),1,4000);			
		 
		 
	CALL grava_historico_agenda_cir(nr_seq_agenda_p,ds_historico_w,Obter_Pessoa_Fisica_Usuario(obter_usuario_ativo,'C'),null,null,null,null,obter_usuario_ativo);
	end;
end if;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
