SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vinc_desvinc_prescr_transcr ( nr_transcricao_p bigint, nr_prescricao_p bigint, ie_vinc_desvinc_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_transcricao_p IS NOT NULL AND nr_transcricao_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ie_vinc_desvinc_p IS NOT NULL AND ie_vinc_desvinc_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	if (ie_vinc_desvinc_p = 'V') then

		update	prescr_medica
		set	nr_seq_transcricao	= nr_transcricao_p,
			nm_usuario	= nm_usuario_p
		where	nr_prescricao	= nr_prescricao_p;

		update	transcricao_prescricao
		set	ie_status		= 'E',
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_transcricao_p;

		CALL atualizar_inicio_transcricao(nr_transcricao_p, nm_usuario_p, 'S');

	elsif (ie_vinc_desvinc_p = 'D') then

		update	prescr_medica
		set	nr_seq_transcricao	 = NULL,
			nm_usuario	= nm_usuario_p
		where	nr_prescricao	= nr_prescricao_p;

		update	transcricao_prescricao
		set	ie_status		= 'S',
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_transcricao_p;

		CALL atualizar_inicio_transcricao(nr_transcricao_p, nm_usuario_p, 'N');

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
