CREATE OR REPLACE PROCEDURE vinc_item_controle_selec_cme ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nr_sequencia_p bigint, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


							
nr_sequencia_w		bigint;
qt_item_w		double precision;
qtd_peso_unitario_w 	bigint;
nr_seq_item_cme_w	bigint;	
nr_seq_cm_item_cont_w	bigint;	


BEGIN					
											
	select	max(d.nr_sequencia),
		max(b.qt_item),
		0 qtd_peso_unitario,	
		max(c.nr_seq_item_cme),
		max(b.nr_sequencia)
	into STRICT    nr_sequencia_w,
		qt_item_w,
		qtd_peso_unitario_w,
		nr_seq_item_cme_w,
		nr_seq_cm_item_cont_w
	from	cm_conjunto_cont a,
		cm_item_cont b,
		cme_item_controle c,
		pepo_item_controle d
	where	a.nr_sequencia		= b.nr_seq_conjunto
	and	a.nr_sequencia 		= nr_sequencia_p
	and 	c.nr_seq_item_cme       = nr_seq_item_p
	and	b.nr_seq_item  		= c.nr_seq_item_cme
	and	c.nr_seq_item  		= d.nr_sequencia
	and	coalesce(a.ie_situacao,'A') 	= 'A';
				


insert into  cirurgia_item_controle(
	        nr_sequencia,
	        dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_cirurgia,
		nr_seq_pepo,
		nr_seq_item_controle,
		ie_acao,
		qt_item,
		qt_peso,
		cd_material,
		nr_seq_lote_fornec,
		dt_validade,
		nr_seq_item_cme,
		ie_situacao,
		nr_seq_cm_item_cont)
values (
		nextval('cirurgia_item_controle_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		CASE WHEN nr_cirurgia_p=0 THEN null  ELSE nr_cirurgia_p END ,
		CASE WHEN nr_seq_pepo_p=0 THEN null  ELSE nr_seq_pepo_p END ,
		nr_sequencia_w,
		1,
		qt_item_w,
		qt_item_w * qtd_peso_unitario_w,
		null,
		null,
		null,
		nr_seq_item_cme_w,
		'A',
		nr_seq_cm_item_cont_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
