CREATE OR REPLACE PROCEDURE vipe_gerar_ordem_apres_grupo ( nr_regra_ordem_p bigint) AS $body$
DECLARE


nr_seq_grupo_w		bigint;
ie_informacao_w		varchar(15);
nr_seq_apres_w		smallint := 999;
ie_acm_sn_final_w	varchar(1);

c01 CURSOR FOR
SELECT	nr_sequencia,
	ie_grupo,
	nr_seq_apres,
	ie_acm_sn_final
from	regra_ordem_grupo_rep
where	nr_seq_regra = nr_regra_ordem_p
order by
	coalesce(nr_seq_apres,999);


BEGIN
if (nr_regra_ordem_p IS NOT NULL AND nr_regra_ordem_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into	nr_seq_grupo_w,
			ie_informacao_w,
			nr_seq_apres_w,
			ie_acm_sn_final_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		update	w_vipe_t
		set	nr_seq_apres_grupo = nr_seq_apres_w
		where	ie_tipo_item = ie_informacao_w;

		CALL vipe_gerar_ordem_apres_inf(nr_seq_grupo_w, ie_acm_sn_final_w, ie_informacao_w);
		end;
	end loop;
	close c01;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
