CREATE OR REPLACE PROCEDURE vipe_obter_dados_item_composto ( nr_prescricao_p bigint, nr_seq_item_ent_p bigint, qt_item_p bigint, nr_prescricoes_p text, cd_item_p text, cd_intervalo_p text, nr_seq_item_p INOUT bigint) AS $body$
DECLARE


nr_prescr_w	bigint;
nr_seq_item_w	bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	nr_prescr_w	:= nr_prescricao_p;
else
	nr_prescr_w	:= Obter_Max_NrPrescricao(nr_prescricoes_p);
end if;

if (nr_seq_item_ent_p IS NOT NULL AND nr_seq_item_ent_p::text <> '') then
	nr_seq_item_w	:= nr_seq_item_ent_p;
else
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_item_w
        from	prescr_material
	where   cd_intervalo 	= cd_intervalo_p
	and     cd_material 	= cd_item_p
	and     ie_agrupador 	= 1
	and     qt_dose 	= qt_item_p
	and     nr_prescricao 	= nr_prescr_w;
end if;

nr_seq_item_p	:= nr_seq_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
