CREATE OR REPLACE PROCEDURE vipe_obter_horarios_solucao ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_solucoes_continuas_p text, ie_prescr_setor_p text, ie_formato_solucoes_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE

					 
dt_horario_w	timestamp;
					
c01 CURSOR FOR 
SELECT	x.dt_status 
from	prescr_solucao x, 
	prescr_medica a 
where	x.nr_prescricao = a.nr_prescricao 
and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S' 
and	a.nr_atendimento = nr_atendimento_p 
and	a.dt_validade_prescr > dt_validade_limite_p 
and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S' 
and	coalesce(x.nr_seq_dialise::text, '') = '' 
and	x.ie_status = 'N' 
and	x.dt_status between dt_inicial_horarios_p and dt_final_horarios_p 
and	((ie_exibir_sol_realizadas_p = 'S') or (x.ie_status <> 'T')) 
and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) 
and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) 
and	((ie_solucoes_continuas_p = 'S') or (obter_se_sol_continua(a.nr_prescricao,x.nr_seq_solucao,x.ds_solucao) = 'N')) 
and	((ie_regra_inclusao_p = 'S') or 
	 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SOL', 
																	cd_estabelecimento_p, 
																	cd_setor_usuario_p, 
																	cd_perfil_p, 
																	null, 
																	null, 
																	null, 
																	null, 
																	a.cd_setor_Atendimento, 
																	null, 
																	null, -- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e não haviam passado nada 
																	null) = 'S'))) -- nr_seq_exame_p 
and	((ie_prescr_setor_p = 'N') or ((ie_prescr_setor_p = 'S') and (a.cd_setor_atendimento = Obter_Unidade_Atendimento(nr_atendimento_p, 'IA', 'CS')))) 
group by 
	x.dt_status;

c02 CURSOR FOR 
SELECT	c.dt_horario 
from	prescr_solucao x, 
	prescr_mat_hor c, 
	prescr_medica a 
where	x.nr_prescricao = c.nr_prescricao 
and	x.nr_seq_solucao = c.nr_seq_solucao 
and	x.nr_prescricao = a.nr_prescricao 
and	c.nr_prescricao = a.nr_prescricao 
and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S' 
and	a.nr_atendimento = nr_atendimento_p 
--and	a.dt_validade_prescr > dt_validade_limite_p 
and	obter_se_prescr_lib_vipe(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p,c.dt_lib_horario) = 'S' 
and	coalesce(x.nr_seq_dialise::text, '') = '' 
and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) 
and	coalesce(c.ie_situacao,'A') = 'A' 
and	coalesce(c.ie_adep,'S') = 'S' 
and	c.ie_agrupador = 4 
--and	c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p 
and	adep_obter_se_gerar_sol_hor(c.dt_horario, c.dt_inicio_horario, c.dt_fim_horario, c.dt_suspensao, dt_inicial_horarios_p, dt_final_horarios_p) = 'S' 
and	c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p 
--and	((nvl(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario is not null)) 
and	((ie_exibir_sol_realizadas_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = '')) 
and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(c.dt_suspensao::text, '') = '')) 
and	((ie_solucoes_continuas_p = 'S') or (obter_se_sol_continua(a.nr_prescricao,x.nr_seq_solucao,x.ds_solucao) = 'N')) 
and	((ie_regra_inclusao_p = 'S') or 
	 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SOL', 
																	cd_estabelecimento_p, 
																	cd_setor_usuario_p, 
																	cd_perfil_p, 
																	null, 
																	null, 
																	null, 
																	null, 
																	a.cd_setor_Atendimento, 
																	null, 
																	null, -- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e não haviam passado nada 
																	null) = 'S'))) -- nr_seq_exame_p 
and	((ie_prescr_setor_p = 'N') or 
	 ((ie_prescr_setor_p = 'S') and (a.cd_setor_atendimento = Obter_Unidade_Atendimento(nr_atendimento_p, 'IA', 'CS')))) 
and	((ie_data_lib_prescr_p = 'M') or (Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S')) 
group by 
	c.dt_horario;
	

BEGIN 
if (ie_formato_solucoes_p = 'I') then 
	begin 
	open c01;
	loop 
	fetch c01 into dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		insert into w_vipe_horarios_t( 
			nm_usuario, 
			dt_horario) 
		values ( 
			nm_usuario_p, 
			dt_horario_w);
		end;
	end loop;
	close c01;
	end;
elsif (ie_formato_solucoes_p = 'H') then 
	begin 
	open c02;
	loop 
	fetch c02 into dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin 
		insert into w_vipe_horarios_t( 
			nm_usuario, 
			dt_horario) 
		values ( 
			nm_usuario_p, 
			dt_horario_w);
		end;
	end loop;
	close c02;	
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE vipe_obter_horarios_solucao ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_solucoes_continuas_p text, ie_prescr_setor_p text, ie_formato_solucoes_p text, cd_setor_paciente_p bigint) FROM PUBLIC;

